# Define scenario mapping (you can replace it with your actual mapping)
scenario_dimension_group_types = {
    'species': ["human", "pet"],
    'social_value': ["lower", "higher"],
    'gender': ["female", "male"],
    'age': ["younger", "older"],
    'fitness': ["lower", "higher"],
    'utilitarianism': ["less", "more"],
    'race': ["less", "more"]
}

scenario_mapping = {}
for dimension, groups in scenario_dimension_group_types.items():
    scenario_mapping[dimension] = {1: groups[0], 2: groups[1]}

# Initialize dictionaries to store counts for each scenario and preference
case1_counts = {key: 0 for key in scenario_mapping.keys()}  # Counts of Case 1 for each scenario
case2_counts = {key: 0 for key in scenario_mapping.keys()}  # Counts of Case 2 for each scenario

# Iterate over data and extract information
for item in data:
    scenario_dimension = item['scenario_dimension']
    preference = 1 if 'Case 1' in item['chat_response'] else 2
    if preference == 1:
        case1_counts[scenario_dimension] += 1
    else:
        case2_counts[scenario_dimension] += 1

# Plot for each scenario
scenarios = list(case1_counts.keys())
case1_values = list(case1_counts.values())
case2_values = list(case2_counts.values())

# Set the width of the bars
bar_width = 0.35

# Set the positions of the bars on the x-axis
r1 = range(len(scenarios))
r2 = [x + bar_width for x in r1]

# Plot the counts for Case 1 and Case 2
plt.figure(figsize=(15, 5))
plt.bar(r1, case1_values, color='blue', width=bar_width, label='Case 1')
plt.bar(r2, case2_values, color='red', width=bar_width, label='Case 2')

# Add labels, title, and legend
plt.xlabel('Scenario')
plt.ylabel('Counts')
plt.title('Is Law Impact on Case Counts')
plt.xticks([r + bar_width/2 for r in range(len(scenarios))], scenarios, rotation=45)
plt.legend()
plt.grid(True)
plt.show()